(function(){'use strict';window.addEventListener('load',function(){var copyBtn=document.querySelector('.js-copy-btn');var iPhoneORiPod=false,iPad=false,safari=false;if(navigator.userAgent.match(/iPhone|iPod/i)){iPhoneORiPod=true;}else if(navigator.userAgent.match(/iPad/i)){iPad=true;}else if(/^((?!chrome).)*safari/i.test(navigator.userAgent)){safari=true;}
if(iPhoneORiPod||iPad||safari){copyBtn.textContent="Select text";}
copyBtn.addEventListener('click',function(){var copyItem=document.querySelector('.text-to-copy');copyItem.select();try{document.execCommand('copy');if(iPhoneORiPod){copyBtn.textContent="Now tap 'Copy'";}else if(iPad){copyBtn.textContent="Now tap the text, then 'Copy'";}else if(safari){copyBtn.textContent="Press Command + C to copy";}else{copyBtn.textContent="Copied!";}}catch(ignore){copyBtn.textContent="Please copy manually";}
copyItem.focus();if(iPhoneORiPod||iPad){copyItem.selectionStart=0;copyItem.selectionEnd=copyItem.textContent.length;}else{copyItem.select();}
copyBtn.disabled=true;});});}());
